import React, { useState, useEffect } from 'react';
import experienceData from '../data/experiences.json'; // Mengimpor data JSON

const Addition = ({ playerData, setPlayerData }) => {
  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [showSkillModal, setShowSkillModal] = useState(false);
  const [showLevelUpModal, setShowLevelUpModal] = useState(false);
  const [levelUpMessage, setLevelUpMessage] = useState('');
  const [skillMessage, setSkillMessage] = useState('');
  const [timer, setTimer] = useState(60); // Timer countdown
  const [timerRunning, setTimerRunning] = useState(false); // Control timer state
  const [timerInterval, setTimerInterval] = useState(null); // Store interval reference

  // Fungsi untuk mendapatkan data experience berdasarkan level pemain
  const getExperienceForLevel = (level) => {
    const levelData = experienceData.find((exp) => exp.level === level);
    return levelData || { min_exp: 0, max_exp: 100 }; // Jika level tidak ada, return default
  };

  useEffect(() => {
    generateRandomProblem();
  }, [playerData.level]);

  // Fungsi untuk menghasilkan soal penjumlahan berdasarkan level pemain
  const generateRandomProblem = () => {
    const level = playerData.level;
    let min = 1;
    let max = 10;

    const experienceForLevel = getExperienceForLevel(level);
    const minExp = experienceForLevel.min_exp;
    const maxExp = experienceForLevel.max_exp;

    // Menentukan batas angka yang digunakan pada soal berdasarkan level pemain
    if (level === 2) {
      min = 10;
      max = 100;
    } else if (level === 3) {
      min = 100;
      max = 1000;
    } else if (level === 4) {
      min = 1000;
      max = 10000;
    } else if (level >= 5) {
      min = 10000;
      max = 100000;
    }

    const num1 = Math.floor(Math.random() * (max - min + 1)) + min;
    const num2 = Math.floor(Math.random() * (max - min + 1)) + min;
    setNum1(num1);
    setNum2(num2);

    // Reset timer setiap soal baru
    setTimer(60);
    startTimer();
  };

  // Fungsi untuk memulai timer
  const startTimer = () => {
    if (timerInterval) clearInterval(timerInterval); // Clear previous interval if exists

    setTimerRunning(true); // Start the timer
    const interval = setInterval(() => {
      setTimer((prevTimer) => {
        if (prevTimer <= 1) {
          clearInterval(interval); // Stop the timer when it reaches 0
          handleTimeUp(); // Handle when time is up
          return 0;
        }
        return prevTimer - 1;
      });
    }, 1000); // Decrease timer every 1 second

    setTimerInterval(interval); // Store the interval ID for later clearing
  };

  // Fungsi ketika waktu habis
  const handleTimeUp = () => {
    if (playerData.hp > 0) {
      const updatedData = { ...playerData };
      updatedData.hp -= 1; // Kurangi 1 HP jika waktu habis
      setPlayerData(updatedData);
      localStorage.setItem('player', JSON.stringify(updatedData)); // Update data player di localStorage
    } else {
      alert('Game Over! HP habis.');
    }
    setUserAnswer(''); // Reset jawaban setelah waktu habis
    generateRandomProblem(); // Generate soal baru setelah waktu habis
  };

  // Fungsi untuk menangani ketika pengguna men-submit jawabannya
  const handleSubmit = () => {
    const correctAnswer = num1 + num2;

    if (Number(userAnswer) === correctAnswer) {
      const prevLevel = playerData.level;
      const prevSkillLevel = playerData.skills?.penjumlahan?.level || 0;

      // Ambil data exp berdasarkan level pemain
      const experienceForLevel = getExperienceForLevel(playerData.level);
      let newExp = playerData.exp + 10; // Menambah 10 exp untuk setiap soal benar
      let newLevel = Math.floor(newExp / experienceForLevel.max_exp) + 1;

      let newSkillExp = (playerData.skills?.penjumlahan?.exp || 0) + 10;
      let newSkillLevel = Math.floor(newSkillExp / 100) + 1;

      const updatedSkills = { ...playerData.skills };
      if (!updatedSkills.penjumlahan) {
        updatedSkills.penjumlahan = {
          exp: newSkillExp,
          level: newSkillLevel
        };
        setSkillMessage('Skill Penjumlahan berhasil ditambahkan!');
        setShowSkillModal(true);
      } else {
        updatedSkills.penjumlahan.exp = newSkillExp;
        updatedSkills.penjumlahan.level = newSkillLevel;
      }

      const updatedData = {
        ...playerData,
        exp: newExp,
        level: newLevel,
        skills: updatedSkills,
      };

      setPlayerData(updatedData);
      localStorage.setItem('player', JSON.stringify(updatedData));

      // Jika level pemain berubah, tampilkan modal level up
      if (newLevel > prevLevel) {
        setLevelUpMessage(`Selamat! Anda naik ke level ${newLevel}.`);
        setShowLevelUpModal(true); // Tampilkan modal level up
      } else {
        setLevelUpMessage('');
      }

      // Reset pesan skill setelah beberapa detik
      setTimeout(() => setSkillMessage(''), 3000);
    } else {
      // Jika jawaban salah, kurangi 1 HP
      const updatedData = { ...playerData };
      if (updatedData.hp > 0) {
        updatedData.hp -= 1;
        setPlayerData(updatedData);
        localStorage.setItem('player', JSON.stringify(updatedData)); // Update data player di localStorage
      } else {
        alert('Game Over! HP habis.');
      }
    }

    setUserAnswer('');
    generateRandomProblem(); // Generate soal baru setelah submit
  };

  // Menangani event keyDown pada input untuk submit dengan Enter
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const closeSkillModal = () => {
    setShowSkillModal(false);
  };

  const closeLevelUpModal = () => {
    setShowLevelUpModal(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h1 className="text-2xl font-semibold text-center mb-4">Ujian Penjumlahan</h1>
        <div className="mb-4">
          <p className="text-center text-xl font-bold">{num1} + {num2} = ?</p>
        </div>
        <div className="mb-4">
          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            onKeyDown={handleKeyDown} // Menambahkan event keyDown untuk Enter
            placeholder="Masukkan jawaban"
            className="w-full p-2 border border-gray-300 rounded-lg mb-2"
          />
        </div>
        <button
          onClick={handleSubmit}
          className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          Submit
        </button>

        {/* Timer */}
        <div className="mt-4 text-center">
          <h3 className="text-lg">Waktu: {timer}s</h3>
        </div>

        {/* Tampilkan skill Penjumlahan */}
        {playerData.skills?.penjumlahan && (
          <div className="mt-4 text-center">
            <h2 className="text-xl font-semibold">Skill Penjumlahan</h2>
            <h3 className="text-lg">Level: {playerData.skills.penjumlahan.level}</h3>
            <p className="text-lg">Exp: {playerData.skills.penjumlahan.exp}</p>
          </div>
        )}

        {/* Modal Skill Berhasil Ditambahkan */}
        {showSkillModal && (
          <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
              <h2 className="text-xl font-semibold">Skill Penjumlahan Ditambahkan!</h2>
              <p className="mt-2">{skillMessage}</p>
              <button
                onClick={closeSkillModal}
                className="mt-4 py-2 px-4 bg-green-500 text-white rounded-lg hover:bg-green-600"
              >
                Tutup
              </button>
            </div>
          </div>
        )}

        {/* Modal Level Up */}
        {showLevelUpModal && (
          <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
              <h2 className="text-xl font-semibold">Level Up!</h2>
              <p className="mt-2">{levelUpMessage}</p>
              <button
                onClick={closeLevelUpModal}
                className="mt-4 py-2 px-4 bg-green-500 text-white rounded-lg hover:bg-green-600"
              >
                Tutup
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Addition;
