import React, { useState, useEffect } from 'react';

function Addition() {
  // State untuk soal, jawaban, waktu, dan status
  const [question, setQuestion] = useState(null);
  const [answer, setAnswer] = useState('');
  const [timer, setTimer] = useState(60); // Waktu dalam detik (1 menit)
  const [isAnswered, setIsAnswered] = useState(false);
  const [isTimeUp, setIsTimeUp] = useState(false);

  // Function untuk menghasilkan soal penjumlahan acak
  const generateQuestion = () => {
    const num1 = Math.floor(Math.random() * 100) + 1;  // Angka pertama acak antara 1-100
    const num2 = Math.floor(Math.random() * 100) + 1;  // Angka kedua acak antara 1-100
    setQuestion({ num1, num2, correctAnswer: num1 + num2 });
  };

  // Function untuk memulai timer
  useEffect(() => {
    // Menjalankan generateQuestion saat komponen pertama kali dimuat
    if (!question) {
      generateQuestion();
    }

    const timerId = setInterval(() => {
      if (timer > 0 && !isAnswered && !isTimeUp) {
        setTimer(prevTimer => prevTimer - 1);
      } else if (timer === 0 && !isAnswered) {
        setIsTimeUp(true);  // Waktu habis
        clearInterval(timerId);
      }
    }, 1000);  // Timer berkurang setiap detik

    return () => clearInterval(timerId);  // Membersihkan interval saat komponen dihapus
  }, [timer, isAnswered, isTimeUp, question]);

  // Function untuk menangani pengisian jawaban
  const handleAnswerChange = (e) => {
    setAnswer(e.target.value);
  };

  // Function untuk menangani pengiriman jawaban
  const handleSubmit = (e) => {
    e.preventDefault();
    if (parseInt(answer) === question.correctAnswer) {
      alert('Jawaban Anda benar!');
      setIsAnswered(true);  // Menandakan soal sudah dijawab dengan benar
      setTimeout(() => {
        generateQuestion();  // Buat soal baru setelah beberapa detik
        setAnswer('');  // Reset jawaban
        setTimer(60);  // Reset timer
        setIsAnswered(false);  // Set soal belum dijawab
        setIsTimeUp(false);  // Reset status waktu habis
      }, 1000); // Menunggu 1 detik sebelum soal baru muncul
    } else {
      alert('Jawaban Anda salah!');
    }
  };

  return (
    <div className="text-center p-6">
      <h2 className="text-3xl font-bold mb-4">Penjumlahan</h2>
      {question ? (
        <div>
          {isTimeUp ? (
            <div>
              <p>Waktu habis! Jawaban yang benar adalah {question.correctAnswer}</p>
              <button
                onClick={() => {
                  generateQuestion(); // Soal langsung berubah setelah waktu habis
                  setAnswer('');  // Reset jawaban
                  setTimer(60);  // Reset timer
                  setIsTimeUp(false); // Reset status waktu habis
                }}
                className="mt-4 px-6 py-3 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none"
              >
                Soal berikutnya
              </button>
            </div>
          ) : (
            <div>
              <p className="text-lg mb-4">
                {question.num1} + {question.num2} = ?
              </p>
              <p className="text-xl mb-4">Waktu tersisa: {timer} detik</p>
              {!isAnswered ? (
                <form onSubmit={handleSubmit}>
                  <input
                    type="number"
                    value={answer}
                    onChange={handleAnswerChange}
                    className="px-4 py-2 border rounded-md"
                    placeholder="Masukkan jawaban"
                    required
                  />
                  <button
                    type="submit"
                    className="ml-4 px-6 py-3 bg-green-500 text-white rounded-md hover:bg-green-600 focus:outline-none"
                  >
                    Kirim Jawaban
                  </button>
                </form>
              ) : (
                <p className="mt-4 text-lg">Jawaban benar! Soal akan berubah dalam beberapa detik.</p>
              )}
            </div>
          )}
        </div>
      ) : (
        <p>Memuat soal...</p>
      )}
    </div>
  );
}

export default Addition;
