import React, { useState, useEffect } from 'react';

const PlayerSettingsModal = ({ closeSettingsModal, playerData, saveSettings }) => {
  const [formData, setFormData] = useState({
    name: '',
    level: 1,
    hp: 1000,
    mp: 500,
    exp: 0,
  });

  // Inisialisasi data form dari `playerData`
  useEffect(() => {
    if (playerData) {
      setFormData({
        name: playerData.name || '',
        level: playerData.level || 1,
        hp: playerData.hp || 1000,
        mp: playerData.mp || 500,
        exp: playerData.exp || 0,
      });
    }
  }, [playerData]);

  // Fungsi untuk menangani perubahan input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'level' || name === 'hp' || name === 'mp' || name === 'exp' ? parseInt(value) : value,
    });
  };

  // Fungsi untuk menangani submit form
  const handleSubmit = (e) => {
    e.preventDefault();

    // Validasi nama
    if (formData.name.trim() === '') {
      alert('Name cannot be empty');
      return;
    }

    saveSettings(formData); // Kirim data ke fungsi di App.js
    closeModal(); // Tutup modal setelah menyimpan
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg w-96">
        <h2 className="text-2xl font-bold mb-4">Player Settings</h2>

        <form onSubmit={handleSubmit}>
          {/* Input Nama */}
          <div className="mb-4">
            <label htmlFor="name" className="block">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          </div>

          {/* Input Level */}
          <div className="mb-4">
            <label htmlFor="level" className="block">Level:</label>
            <input
              type="number"
              id="level"
              name="level"
              value={formData.level}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          </div>

          {/* Input HP */}
          <div className="mb-4">
            <label htmlFor="hp" className="block">HP:</label>
            <input
              type="number"
              id="hp"
              name="hp"
              value={formData.hp}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          </div>

          {/* Input MP */}
          <div className="mb-4">
            <label htmlFor="mp" className="block">MP:</label>
            <input
              type="number"
              id="mp"
              name="mp"
              value={formData.mp}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          </div>

          {/* Input EXP */}
          <div className="mb-4">
            <label htmlFor="exp" className="block">EXP:</label>
            <input
              type="number"
              id="exp"
              name="exp"
              value={formData.exp}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          </div>

          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-lg mt-4 w-full">
            Save Settings
          </button>
        </form>
      </div>
    </div>
  );
};

export default PlayerSettingsModal;
