import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Addition() {
  const [question, setQuestion] = useState(null);
  const [answer, setAnswer] = useState('');
  const [timer, setTimer] = useState(60);
  const [isAnswered, setIsAnswered] = useState(false);
  const [isTimeUp, setIsTimeUp] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);

  const generateQuestion = () => {
    const num1 = Math.floor(Math.random() * 100) + 1;
    const num2 = Math.floor(Math.random() * 100) + 1;
    setQuestion({ num1, num2, correctAnswer: num1 + num2 });
  };

  useEffect(() => {
    if (!gameStarted) return;

    if (!question) {
      generateQuestion();
    }

    const timerId = setInterval(() => {
      if (timer > 0 && !isAnswered && !isTimeUp) {
        setTimer(prevTimer => prevTimer - 1);
      } else if (timer === 0 && !isAnswered) {
        setIsTimeUp(true);
        clearInterval(timerId);
      }
    }, 1000);

    return () => clearInterval(timerId);
  }, [timer, isAnswered, isTimeUp, question, gameStarted]);

  const handleAnswerChange = (e) => {
    setAnswer(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (parseInt(answer) === question.correctAnswer) {
      toast.success('Jawaban Anda benar!');
      setIsAnswered(true);
      setTimeout(() => {
        generateQuestion();
        setAnswer('');
        setTimer(60);
        setIsAnswered(false);
        setIsTimeUp(false);
      }, 1000);
    } else {
      toast.error('Jawaban Anda salah!');
    }
  };

  const startGame = () => {
    setGameStarted(true);
    setIsAnswered(false);
    setIsTimeUp(false);
    setTimer(60);
  };

  return (
    <div className="text-center p-6">
      <h2 className="text-3xl font-bold mb-4">Penjumlahan</h2>
      {question ? (
        <div>
          {isTimeUp ? (
            <div>
              <p>Waktu habis! Jawaban yang benar adalah {question.correctAnswer}</p>
              <button
                onClick={() => {
                  generateQuestion();
                  setAnswer('');
                  setTimer(60);
                  setIsTimeUp(false);
                }}
                className="mt-4 px-6 py-3 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              >
                Soal berikutnya
              </button>
            </div>
          ) : (
            <div>
              <p className="text-lg mb-4">{question.num1} + {question.num2} = ?</p>
              <p className="text-xl mb-4">Waktu tersisa: {timer} detik</p>
              {!isAnswered ? (
                <form onSubmit={handleSubmit}>
                  <input
                    type="number"
                    value={answer}
                    onChange={handleAnswerChange}
                    className="px-4 py-2 border rounded-md"
                    placeholder="Masukkan jawaban"
                    required
                  />
                </form>
              ) : (
                <p className="mt-4 text-lg">Jawaban benar! Soal akan berubah dalam beberapa detik.</p>
              )}
            </div>
          )}
        </div>
      ) : (
        <p>Memuat soal...</p>
      )}

      {!gameStarted && (
        <button
          onClick={startGame}
          className="mt-6 px-6 py-3 bg-green-500 text-white rounded-md hover:bg-green-600"
        >
          Mulai Permainan
        </button>
      )}

      <ToastContainer />
    </div>
  );
}

export default Addition;
