import React, { useState } from 'react';

function Addition() {
  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [result, setResult] = useState(null);

  const handleAdd = () => {
    setResult(num1 + num2);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h1 className="text-2xl font-semibold text-center mb-4">Halaman Penjumlahan</h1>
        <div className="mb-4">
          <input
            type="number"
            value={num1}
            onChange={(e) => setNum1(Number(e.target.value))}
            placeholder="Masukkan angka pertama"
            className="w-full p-2 border border-gray-300 rounded-lg mb-2"
          />
        </div>
        <div className="mb-4">
          <input
            type="number"
            value={num2}
            onChange={(e) => setNum2(Number(e.target.value))}
            placeholder="Masukkan angka kedua"
            className="w-full p-2 border border-gray-300 rounded-lg mb-2"
          />
        </div>
        <button
          onClick={handleAdd}
          className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          Tambah
        </button>
        {result !== null && (
          <h2 className="text-xl text-center mt-4 font-bold">Hasil: {result}</h2>
        )}
      </div>
    </div>
  );
}

export default Addition;
