import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Navbar from './components/Navbar';
import PlayerProfileModal from './components/PlayerProfileModal';
import PlayerSettingsModal from './components/PlayerSettingsModal';
import DeleteAccountModal from './components/DeleteAccountModal';
import PlayerModal from './components/PlayerModal';
import AddPage from './pages/AddPage';
import SubtractPage from './pages/SubtractPage';
import MultiplyPage from './pages/MultiplyPage';
import DividePage from './pages/DividePage';
import { FaPlusCircle, FaMinusCircle, FaTimesCircle, FaDivide } from 'react-icons/fa';

const App = () => {
  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);
  const [isSettingsModalOpen, setIsSettingsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isPlayerModalOpen, setIsPlayerModalOpen] = useState(false);

  // Gabungkan semua data pemain ke dalam satu objek
  const [playerData, setPlayerData] = useState({
    name: '',
    level: 1,
    hp: 1000,
    mp: 500,
    exp: 0,
    skills: ['Addition', 'Subtraction', 'Multiplication', 'Division'],
  });

  // Fungsi untuk menyimpan data ke localStorage
  const savePlayerDataToLocalStorage = (data) => {
    localStorage.setItem('playerData', JSON.stringify(data));
  };

  // Fungsi untuk menyimpan nama pemain
  const savePlayerName = (name) => {
    const updatedData = { ...playerData, name };
    setPlayerData(updatedData);
    savePlayerDataToLocalStorage(updatedData);
  };

  // Fungsi untuk menyimpan pengaturan pemain
  const savePlayerSettings = (settings) => {
    const updatedData = { ...playerData, ...settings };
    setPlayerData(updatedData);
    savePlayerDataToLocalStorage(updatedData);
  };

  // Fungsi untuk menyimpan perubahan profil pemain
  const savePlayerProfile = (profileData) => {
    const updatedData = { ...playerData, ...profileData };
    setPlayerData(updatedData);
    savePlayerDataToLocalStorage(updatedData);
  };

  // Fungsi untuk menghapus akun pemain
  const confirmDeleteAccount = () => {
    localStorage.removeItem('playerData');
    setPlayerData({
      name: '',
      level: 1,
      hp: 1000,
      mp: 500,
      exp: 0,
      skills: ['Addition', 'Subtraction', 'Multiplication', 'Division'],
    });
    closeDeleteModal();
  };

  // Load data pemain dari localStorage saat aplikasi dimuat
  useEffect(() => {
    const savedData = JSON.parse(localStorage.getItem('playerData'));
    if (savedData) {
      setPlayerData(savedData);
    } else {
      openPlayerModal();
    }
  }, []);

  // Fungsi untuk membuka/menutup modal
  const openProfileModal = () => setIsProfileModalOpen(true);
  const closeProfileModal = () => setIsProfileModalOpen(false);
  const openSettingsModal = () => setIsSettingsModalOpen(true);
  const closeSettingsModal = () => setIsSettingsModalOpen(false);
  const openDeleteModal = () => setIsDeleteModalOpen(true);
  const closeDeleteModal = () => setIsDeleteModalOpen(false);
  const openPlayerModal = () => setIsPlayerModalOpen(true);
  const closePlayerModal = () => setIsPlayerModalOpen(false);

  return (
    <Router>
      <div>
        <Navbar
          playerName={playerData.name}
          openProfileModal={openProfileModal}
          openSettingsModal={openSettingsModal}
          openDeleteModal={openDeleteModal}
        />{/* Modal untuk memasukkan nama pemain */}
        {isPlayerModalOpen && (
          <PlayerSettingsModal
          closeModal={closeSettingsModal}
          playerData={playerData}
          saveSettings={updatePlayerData}
        />
        )}

        {/* Modal untuk profil pemain */}
        {isProfileModalOpen && (
          <PlayerProfileModal
            closeModal={closeProfileModal}
            playerData={playerData}
            updatePlayerData={updatePlayerData}
          />
        )}

        {/* Modal untuk pengaturan pemain */}
        {isSettingsModalOpen && (
          <PlayerSettingsModal
            closeModal={closeSettingsModal}
            playerData={playerData}
            updatePlayerData={updatePlayerData}
          />
        )}

        {/* Modal untuk konfirmasi penghapusan akun */}
        {isDeleteModalOpen && (
          <DeleteAccountModal
            closeModal={closeDeleteModal}
            deletePlayerData={deletePlayerData}
          />
        )}

        <div className="p-6">
          <h1 className="text-3xl font-bold">Welcome to the App</h1>

          {/* Pilihan operasi matematika */}
          <div className="mt-6">
            <h3 className="text-xl">Choose an Operation:</h3>
            <div className="flex flex-col space-y-4 mt-4">
              <Link
                to="/add"
                className="flex items-center px-6 py-3 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-600 focus:outline-none"
              >
                <FaPlusCircle className="mr-2" /> Addition
              </Link>
              <Link
                to="/subtract"
                className="flex items-center px-6 py-3 bg-red-500 text-white rounded-lg shadow-md hover:bg-red-600 focus:outline-none"
              >
                <FaMinusCircle className="mr-2" /> Subtraction
              </Link>
              <Link
                to="/multiply"
                className="flex items-center px-6 py-3 bg-green-500 text-white rounded-lg shadow-md hover:bg-green-600 focus:outline-none"
              >
                <FaTimesCircle className="mr-2" /> Multiplication
              </Link>
              <Link
                to="/divide"
                className="flex items-center px-6 py-3 bg-yellow-500 text-white rounded-lg shadow-md hover:bg-yellow-600 focus:outline-none"
              >
                <FaDivide className="mr-2" /> Division
              </Link>
            </div>
          </div>
        </div>

        {/* Routes untuk halaman operasi matematika */}
        <Routes>
          <Route path="/add" element={<AddPage />} />
          <Route path="/subtract" element={<SubtractPage />} />
          <Route path="/multiply" element={<MultiplyPage />} />
          <Route path="/divide" element={<DividePage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
