import React from 'react';

const LevelSection = ({ level, exp }) => (
  <div className="mt-4 text-center">
    <h2 className="text-xl font-semibold">Level Pemain: {level}</h2>
    <h3 className="text-lg">Exp Pemain: {exp}</h3>
    {/* Progress Bar untuk Level Pemain */}
    <div className="mt-2">
      <div className="w-full bg-gray-200 rounded-full h-2.5">
        <div
          className="bg-green-500 h-2.5 rounded-full"
          style={{
            width: `${(exp / (experienceData[level - 1]?.experience || 100)) * 100}%`, // Progress bar sesuai dengan level
          }}
        ></div>
      </div>
    </div>
  </div>
);

export default LevelSection;
