import React, { useState, useEffect } from 'react';
import { FaUserCircle } from 'react-icons/fa'; // Import the user icon

function App() {
  // Initialize state
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isProfileModalOpen, setIsProfileModalOpen] = useState(false); // State for profile modal
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [gender, setGender] = useState('');
  const [birthDate, setBirthDate] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login state
  const [playerData, setPlayerData] = useState(null); // Store player data from localStorage

  // Function to open the modal for login form
  const handleOpenModal = () => {
    // Reset form fields when modal is opened (this will clear any existing data)
    setFirstName('');
    setLastName('');
    setGender('');
    setBirthDate('');
    setIsModalOpen(true);
  };

  // Function to close the login modal
  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  // Function to handle form submission (login or update profile)
  const handleSubmit = (e) => {
    e.preventDefault();

    const player = {
      firstName,
      lastName,
      gender,
      birthDate,
    };

    // Save player data to localStorage
    localStorage.setItem('player', JSON.stringify(player));
    setIsLoggedIn(true);
    setPlayerData(player);

    // Clear the form after submission
    setFirstName('');
    setLastName('');
    setGender('');
    setBirthDate('');
    handleCloseModal();

    alert('Data player telah disimpan!');
  };

  // Retrieve player data from localStorage when the app loads
  useEffect(() => {
    const savedPlayerData = localStorage.getItem('player');
    if (savedPlayerData) {
      const player = JSON.parse(savedPlayerData);
      setPlayerData(player);
      setIsLoggedIn(true);
    }
  }, []);

  // Function to handle deleting the account (clear localStorage)
  const handleDeleteAccount = () => {
    localStorage.removeItem('player');
    setIsLoggedIn(false);
    setPlayerData(null);
    alert('Akun telah dihapus!');
  };

  // Function to handle opening the profile modal
  const handleViewProfile = () => {
    setIsProfileModalOpen(true); // Open profile modal
  };

  // Function to close the profile modal
  const handleCloseProfileModal = () => {
    setIsProfileModalOpen(false);
  };

  // Function to handle opening the profile editing modal and pre-filling the data
  const handleEditProfile = () => {
    if (playerData) {
      // Pre-fill the form fields with playerData
      setFirstName(playerData.firstName);
      setLastName(playerData.lastName);
      setGender(playerData.gender);
      setBirthDate(playerData.birthDate);
      setIsModalOpen(true); // Open the modal to edit player data
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav className="bg-blue-600 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-white text-xl font-bold">Login App</h1>

          {isLoggedIn ? (
            <div className="relative">
              <button
                className="text-white bg-green-500 px-4 py-2 rounded-md flex items-center"
              >
                <FaUserCircle className="mr-2 text-xl" />
                {playerData?.firstName}
              </button>

              {/* Dropdown Menu */}
              <div className="absolute right-0 mt-2 bg-white border rounded-md shadow-lg w-48">
                <button
                  onClick={handleViewProfile}
                  className="block w-full px-4 py-2 text-gray-700 hover:bg-gray-100"
                >
                  View Profile
                </button>
                <button
                  onClick={handleEditProfile}
                  className="block w-full px-4 py-2 text-gray-700 hover:bg-gray-100"
                >
                  Edit Profile
                </button>
                <button
                  onClick={handleDeleteAccount}
                  className="block w-full px-4 py-2 text-red-500 hover:bg-red-100"
                >
                  Delete Account
                </button>
              </div>
            </div>
          ) : (
            <button
              onClick={handleOpenModal}
              className="text-white bg-green-500 px-4 py-2 rounded-md hover:bg-green-400"
            >
              Login
            </button>
          )}
        </div>
      </nav>

      {/* Modal (for Login and Edit Profile) */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-semibold mb-4">
                {playerData ? 'Edit Profile' : 'Login Form'}
              </h2>
              <button
                onClick={handleCloseModal}
                className="text-red-500 hover:text-red-400"
              >
                Tutup
              </button>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="mb-4 flex space-x-4">
                {/* Nama Depan */}
                <div className="flex-1">
                  <label htmlFor="firstName" className="block text-gray-700">Nama Depan</label>
                  <input
                    type="text"
                    id="firstName"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    required
                    placeholder="Masukkan nama depan"
                  />
                </div>

                {/* Nama Belakang */}
                <div className="flex-1">
                  <label htmlFor="lastName" className="block text-gray-700">Nama Belakang</label>
                  <input
                    type="text"
                    id="lastName"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md"
                    required
                    placeholder="Masukkan nama belakang"
                  />
                </div>
              </div>

              {/* Gender (Select Dropdown) */}
              <div className="mb-4">
                <label htmlFor="gender" className="block text-gray-700">Gender</label>
                <select
                  id="gender"
                  value={gender}
                  onChange={(e) => setGender(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  required
                >
                  <option value="">Pilih Gender</option>
                  <option value="Laki-laki">Laki-laki</option>
                  <option value="Perempuan">Perempuan</option>
                </select>
              </div>

              {/* Tanggal Lahir */}
              <div className="mb-4">
                <label htmlFor="birthDate" className="block text-gray-700">Tanggal Lahir</label>
                <input
                  type="date"
                  id="birthDate"
                  value={birthDate}
                  onChange={(e) => setBirthDate(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  required
                />
              </div>

              {/* Submit Button */}
              <button
                type="submit"
                className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-400"
              >
                {playerData ? 'Update Profile' : 'Login'}
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Profile Modal */}
      {isProfileModalOpen && playerData && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-2xl font-semibold mb-4">Profile</h2>
            <p><strong>First Name:</strong> {playerData.firstName}</p>
            <p><strong>Last Name:</strong> {playerData.lastName}</p>
            <p><strong>Gender:</strong> {playerData.gender}</p>
            <p><strong>Birth Date:</strong> {playerData.birthDate}</p>
            <button
              onClick={handleCloseProfileModal}
              className="mt-4 w-full bg-red-500 text-white py-2 rounded-md hover:bg-red-400"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
