import React, { useState, useEffect } from 'react';

const Addition = ({ playerData, setPlayerData }) => {
  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [showSkillModal, setShowSkillModal] = useState(false); // State untuk kontrol modal

  useEffect(() => {
    generateRandomProblem();
  }, []);

  const generateRandomProblem = () => {
    const num1 = Math.floor(Math.random() * 10) + 1;
    const num2 = Math.floor(Math.random() * 10) + 1;
    setNum1(num1);
    setNum2(num2);
  };

  const handleSubmit = () => {
    const correctAnswer = num1 + num2;
    if (Number(userAnswer) === correctAnswer) {
      // Simpan level dan exp awal
      const prevLevel = playerData.level;
      const prevSkillLevel = playerData.skills?.penjumlahan?.level || 0;

      // Menambah exp jika jawaban benar
      let newExp = playerData.exp + 10; // Menambah 10 exp per soal benar
      let newLevel = Math.floor(newExp / 100) + 1; // Setiap 100 exp naik level

      // Menambah exp pada skill "Penjumlahan"
      let newSkillExp = (playerData.skills?.penjumlahan?.exp || 0) + 10; // Jika skill belum ada, mulai dari 10
      let newSkillLevel = Math.floor(newSkillExp / 100) + 1; // Menentukan level skill Penjumlahan

      // Jika skill "Penjumlahan" tidak ada, buat skill tersebut dan tampilkan modal
      const updatedSkills = { ...playerData.skills }; // Salin skills player
      if (!updatedSkills.penjumlahan) {
        updatedSkills.penjumlahan = {
          exp: newSkillExp,
          level: newSkillLevel
        };
        setShowSkillModal(true); // Tampilkan modal ketika skill pertama kali ditambahkan
      } else {
        // Update exp skill jika sudah ada
        updatedSkills.penjumlahan.exp = newSkillExp;
        updatedSkills.penjumlahan.level = newSkillLevel;
      }

      // Update player data dengan exp dan level baru
      const updatedData = {
        ...playerData,
        exp: newExp,
        level: newLevel,
        skills: updatedSkills,
      };

      setPlayerData(updatedData);
      localStorage.setItem('player', JSON.stringify(updatedData));

      // Tampilkan modal jika level pemain atau skill naik
      if (newLevel > prevLevel || newSkillLevel > prevSkillLevel) {
        setShowSkillModal(true);
      }
    } else {
      // Memberi tahu jawaban salah, tetapi tidak dengan alert
      // (Anda bisa menggunakan modal atau feedback lain di sini)
      alert('Jawaban salah. Coba lagi!');
    }

    setUserAnswer('');
    generateRandomProblem(); // Generate new problem
  };

  const closeSkillModal = () => {
    setShowSkillModal(false); // Menutup modal
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h1 className="text-2xl font-semibold text-center mb-4">Ujian Penjumlahan</h1>
        <div className="mb-4">
          <p className="text-center text-xl font-bold">{num1} + {num2} = ?</p>
        </div>
        <div className="mb-4">
          <input
            type="number"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            placeholder="Masukkan jawaban"
            className="w-full p-2 border border-gray-300 rounded-lg mb-2"
          />
        </div>
        <button
          onClick={handleSubmit}
          className="w-full py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
        >
          Submit
        </button>

        {/* Tampilkan skill Penjumlahan */}
        {playerData.skills?.penjumlahan && (
          <div className="mt-4 text-center">
            <h2 className="text-xl font-semibold">Skill Penjumlahan</h2>
            <h3 className="text-lg">Level: {playerData.skills.penjumlahan.level}</h3>
            <p className="text-lg">Exp: {playerData.skills.penjumlahan.exp}</p>
          </div>
        )}

        {/* Tampilkan level umum dan exp */}
        {playerData.exp !== undefined && (
          <div className="mt-4 text-center">
            <h2 className="text-xl font-semibold">Level Pemain: {playerData.level}</h2>
            <h3 className="text-lg">Exp Pemain: {playerData.exp}</h3>
          </div>
        )}
      </div>

      {/* Modal Skill Berhasil Ditambahkan */}
      {showSkillModal && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
            <h2 className="text-xl font-semibold">Skill Penjumlahan Ditambahkan!</h2>
            <p className="mt-2">Skill Penjumlahan berhasil ditambahkan ke karakter Anda. Mulai tingkatkan skill Anda!</p>
            <button
              onClick={closeSkillModal}
              className="mt-4 py-2 px-4 bg-green-500 text-white rounded-lg hover:bg-green-600"
            >
              Tutup
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Addition;
