import React, { useState, useEffect } from 'react';

const PlayerSettingsModal = ({ closeModal, saveSettings }) => {
  const [name, setName] = useState('');
  const [level, setLevel] = useState(1);
  const [hp, setHp] = useState(1000);
  const [mp, setMp] = useState(500);

  // Ambil data player dari props (atau localStorage jika belum ada di props)
  useEffect(() => {
    const savedProfile = JSON.parse(localStorage.getItem('playerProfile'));
    if (savedProfile) {
      setName(savedProfile.name);
      setLevel(savedProfile.level);
      setHp(savedProfile.hp);
      setMp(savedProfile.mp);
    }
  }, []);

  // Fungsi untuk menangani perubahan input
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'name') setName(value);
    if (name === 'level') setLevel(value);
    if (name === 'hp') setHp(value);
    if (name === 'mp') setMp(value);
  };

  // Fungsi untuk menangani submit form
  const handleSubmit = (e) => {
    e.preventDefault();
    const playerSettings = { name, level, hp, mp };
    saveSettings(playerSettings); // Simpan pengaturan player ke App.js dan localStorage
    closeModal(); // Tutup modal setelah data disimpan
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg w-96">
        <h2 className="text-2xl font-bold mb-4">Player Settings</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="name" className="block">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              value={name}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="level" className="block">Level:</label>
            <input
              type="number"
              id="level"
              name="level"
              value={level}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"disabled
            />
          </div>
          <div className="mb-4">
            <label htmlFor="hp" className="block">HP:</label>
            <input
              type="number"
              id="hp"
              name="hp"
              value={hp}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md" disabled
            />
          </div>
          <div className="mb-4">
            <label htmlFor="mp" className="block">MP:</label>
            <input
              type="number"
              id="mp"
              name="mp"
              value={mp}
              onChange={handleChange}
              className="w-full px-3 py-2 border border-gray-300 rounded-md"disabled
            />
          </div>
          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-lg mt-4 w-full">
            Save Settings
          </button>
        </form>
      </div>
    </div>
  );
};

export default PlayerSettingsModal;
